targets.expr:
(
  sum by (http_route, http_request_method) (
    rate(http_server_request_duration_seconds_sum{
      service_name=~"$service",
      deployment_environment=~"$environment",
      http_route!=""
    }[$__interval])
  )
  /
  clamp_min(
    sum by (http_route, http_request_method) (
      rate(http_server_request_duration_seconds_count{
        service_name=~"$service",
        deployment_environment=~"$environment",
        http_route!=""
      }[$__interval])
    ),
    1
  )
)
